import React, { Component } from "react";
import { ProductConsumer } from "../context";
import PropTypes from "prop-types";
import Modal from "Modal";

export default class Product extends Component {
  render() {
    const { id, title, img, price } = this.props.product;

    return (
      <div className="col-8 mx-auto col-lg-4 col-md-4">
        <div className="card mt-3">
          <ProductConsumer>
            {value => (
              <div className="img-container p-4">
                <div className="coffeType">
                  <img src={img} alt="coffe type" className="card-img-top" />
                  <button onClick={() => value.openModal(id)}>clickMe</button>
                  <div className="d-flex justify-content-between pb-3">
                    <h5 className="title">{title}</h5>
                    <h5 className="price">{price}$</h5>
                  </div>
                </div>
              </div>
            )}
          </ProductConsumer>
        </div>
      </div>
    );
  }
}
Product.propTypes = {
  product: PropTypes.shape({
    id: PropTypes.number,
    img: PropTypes.string,
    title: PropTypes.string,
    price: PropTypes.number,
    inCart: PropTypes.bool
  }).isRequired
};
