import React from "react";

export default function CartItem({ item, value }) {
  //how many items are in the cart - of the specifice item - count
  const { id, title, img, price, count } = item;
  //in the value we have all the methods
  const { increment, decrement, removeItem, handleSubmit } = value;
  return (
    //every item we're going to return as a separate row
    <div className="row my-1 text-capitalize text-center">
      <div className="col-10 mx-auto col-lg-2">
        <img
          src={img}
          style={{ width: "5rem", height: "5rem" }}
          className="img-fluid"
          alt="product"
        />
      </div>
      <div className="col-10 mx-auto col-lg-2 py-3">
        <span className="d-lg-none">product: </span>
        {title}
        {handleSubmit}
      </div>
      <div className="col-10 mx-auto col-lg-2 py-3">
        <span className="d-lg-none">price: </span>
        {price}$
      </div>
      <div className="col-10 mx-auto col-lg-2 my-2 my-lg-2 py-3">
        <div className="d-flex justify-content-center">
          <div>
            <span className="btn btn-black mx-1" onClick={() => decrement(id)}>
              -
            </span>
            <span className="btn btn-black mx-1">{count}</span>
            <span className="btn btn-black mx-1" onClick={() => increment(id)}>
              +
            </span>
          </div>
        </div>
      </div>
      <div className="col-10 mx-auto col-lg-2 py-3">
        <div className="cart-icon" onClick={() => removeItem(id)}>
          <i className="fas fa-trash"></i>
        </div>
      </div>
      <div className="col-10 mx-auto col-lg-2 py-3">
        <strong>item total: ${price * count}</strong>
      </div>
    </div>
  );
}
